                    <Mail className="w-4 h-4 mr-2" />
                    <span>vemulapallinehith@gmail.com</span>
                  </div>
                  <div className="flex items-center">
                    <span>+44 7936 634011</span>
                  </div>
                </div>
              </div>
            </StaggerItem>

            {/* Action Buttons */}
            <StaggerItem>
              <div className="flex flex-wrap justify-center gap-4 mb-8">
